import platform
from typing import Optional

from pydoll.browser.base import Browser
from pydoll.browser.managers import BrowserOptionsManager
from pydoll.browser.options import Options


class Chrome(Browser):
    def __init__(
        self,
        options: Optional[Options] = None,
        connection_port: Optional[int] = None,
    ):
        super().__init__(options, connection_port)

    @staticmethod
    def _get_default_binary_location():
        os_name = platform.system()
        browser_paths = {
            'Windows':
                r'C:\Program Files\Google\Chrome\Application\chrome.exe',
            'Linux':
                '/usr/bin/google-chrome',
            'Darwin':
                '/Applications/Google Chrome.app/Contents/MacOS/Google Chrome'
        }

        browser_path = browser_paths.get(os_name)

        if not browser_path:
            raise ValueError('Unsupported OS')

        return BrowserOptionsManager.validate_browser_path(
            browser_path
        )

import os
import shutil
import subprocess
from contextlib import suppress
from tempfile import TemporaryDirectory

from pydoll.browser.options import Options


class ProxyManager:
    def __init__(self, options):
        self.options = options

    def get_proxy_credentials(self) -> tuple[bool, tuple[str, str]]:
        """
        Configura as configurações de proxy e extrai credenciais se presentes.

        Returns:
            tuple[bool, tuple[str, str]]: (private_proxy, (username, password))
        """
        private_proxy = False
        credentials = (None, None)

        proxy_arg = self._find_proxy_argument()

        if proxy_arg is not None:
            index, proxy_value = proxy_arg
            has_credentials, username, password, clean_proxy = (
                self._parse_proxy(proxy_value)
            )

            if has_credentials:
                self._update_proxy_argument(index, clean_proxy)
                private_proxy = True
                credentials = (username, password)

        return private_proxy, credentials

    def _find_proxy_argument(self) -> tuple[int, str] | None:
        """Encontra o primeiro argumento --proxy-server válido"""
        for index, arg in enumerate(self.options.arguments):
            if arg.startswith('--proxy-server='):
                return index, arg.split('=', 1)[1]
        return None

    @staticmethod
    def _parse_proxy(proxy_value: str) -> tuple[bool, str, str, str]:
        """Extrai credenciais e limpa o valor do proxy"""
        if '@' not in proxy_value:
            return False, None, None, proxy_value

        try:
            creds_part, server_part = proxy_value.split('@', 1)
            username, password = creds_part.split(':', 1)
            return True, username, password, server_part
        except ValueError:
            return False, None, None, proxy_value

    def _update_proxy_argument(self, index: int, clean_proxy: str) -> None:
        """Atualiza a lista de argumentos com proxy limpo"""
        self.options.arguments[index] = f'--proxy-server={clean_proxy}'


class BrowserProcessManager:
    def __init__(self, process_creator=None):
        self._process_creator = (
            process_creator or self._default_process_creator
        )
        self._process = None

    def start_browser_process(
        self, binary_location: str, port: int, arguments: list
    ) -> None:
        """Inicia o processo do navegador"""
        self._process = self._process_creator([
            binary_location,
            f'--remote-debugging-port={port}',
            *arguments,
        ])
        return self._process

    @staticmethod
    def _default_process_creator(command: list[str]):
        return subprocess.Popen(
            command, stdout=subprocess.PIPE, stderr=subprocess.PIPE
        )

    def stop_process(self):
        """Para o processo do navegador se estiver em execução"""
        if self._process:
            self._process.terminate()


class TempDirectoryManager:
    def __init__(self, temp_dir_factory=TemporaryDirectory):
        self._temp_dir_factory = temp_dir_factory
        self._temp_dirs = []

    def create_temp_dir(self):
        """
        Cria um diretório temporário para a instância do navegador.

        Returns:
            TemporaryDirectory: O diretório temporário.
        """
        temp_dir = self._temp_dir_factory()
        self._temp_dirs.append(temp_dir)
        return temp_dir

    def cleanup(self):
        """Limpa todos os diretórios temporários"""
        for temp_dir in self._temp_dirs:
            with suppress(OSError):
                shutil.rmtree(temp_dir.name)


class BrowserOptionsManager:
    @staticmethod
    def initialize_options(options: Options | None) -> Options:
        """
        Inicializa as opções para o navegador.

        Args:
            options (Options | None): Uma instância da classe Options ou None.

        Returns:
            Options: A instância de opções inicializada.
        """
        if options is None:
            return Options()
        if not isinstance(options, Options):
            raise ValueError('Invalid options')
        return options

    @staticmethod
    def add_default_arguments(options: Options):
        """Adiciona argumentos padrão aos argumentos fornecidos"""
        options.arguments.append('--no-first-run')
        options.arguments.append('--no-default-browser-check')

    @staticmethod
    def validate_browser_path(path: str) -> str:
        """
        Valida o caminho fornecido do navegador.

        Args:
            path (str): O caminho do arquivo executável do navegador.

        Returns:
            str: O caminho do navegador validado.
        """
        if not os.path.exists(path):
            raise ValueError(f'Browser not found: {path}')
        return path

class BrowserCommands: """ BrowserCommands class provides a set of commands to interact with the browser's main functionality based on CDP. These commands allow for managing browser windows, such as closing windows, retrieving window IDs, and adjusting window bounds (size and state).
The following operations can be performed: - Close the browser. - Get the ID of the current window. - Set the size and position of a specific window. - Maximize or minimize a specific window.
Each method generates a command that can be sent to the browser as part of the communication with the browser's underlying API. """
CLOSE = {'method': 'Browser.close'} GET_WINDOW_ID = {'method': 'Browser.WindowID'} SET_WINDOW_BOUNDS_TEMPLATE = { 'method': 'Browser.setWindowBounds', 'params': {}, } SET_DOWNLOAD_BEHAVIOR = { 'method': 'Browser.setDownloadBehavior', 'params': {}, }
@classmethod def set_download_path(cls, path: str) -> dict: """ Generates the command to set the download path for the browser.
Args: path (str): The path to set for downloads.
Returns: dict: The command to be sent to the browser. """ command = cls.SET_DOWNLOAD_BEHAVIOR.copy() command['params']['behavior'] = 'allow' command['params']['downloadPath'] = path return command
@classmethod def close(cls) -> dict: """ Generates the command to close the browser.
Returns: dict: The command to be sent to the browser. """ return cls.CLOSE
@classmethod def get_window_id(cls) -> dict: """ Generates the command to get the ID of the current window.
Returns: dict: The command to be sent to the browser. """ return cls.GET_WINDOW_ID
@classmethod def set_window_bounds(cls, window_id: int, bounds: dict) -> dict: """ Generates the command to set the bounds of a window.
Args: window_id (int): The ID of the window to set the bounds for. bounds (dict): The bounds to set for the window, which should include width, height, and optionally x and y coordinates.
Returns: dict: The command to be sent to the browser. """ command = cls.SET_WINDOW_BOUNDS_TEMPLATE.copy() command['params']['windowId'] = window_id command['params']['bounds'] = bounds return command
@classmethod def set_window_maximized(cls, window_id: int) -> dict: """ Generates the command to maximize a window.
Args: window_id (int): The ID of the window to maximize.
Returns: dict: The command to be sent to the browser. """ return cls.set_window_bounds(window_id, {'windowState': 'maximized'})
@classmethod def set_window_minimized(cls, window_id: int) -> dict: """ Generates the command to minimize a window.
Args: window_id (int): The ID of the window to minimize.
Returns: dict: The command to be sent to the browser. """ return cls.set_window_bounds(window_id, {'windowState': 'minimized'})

import copy
from typing import Literal

from pydoll.commands.runtime import RuntimeCommands
from pydoll.constants import By, Scripts


class DomCommands:
    """
    A class to define commands for interacting with the Document
    Object Model (DOM) using the Chrome DevTools Protocol (CDP).
    The commands allow for various operations on DOM nodes,
    such as enabling the DOM domain, retrieving the
    DOM document, describing nodes, and querying elements.

    Attributes:
        SelectorType (Literal): A type definition for supported selector types.
    """

    SelectorType = Literal[
        By.CSS_SELECTOR, By.XPATH, By.CLASS_NAME, By.ID, By.TAG_NAME
    ]

    ENABLE = {'method': 'DOM.enable'}
    DOM_DOCUMENT = {'method': 'DOM.getDocument'}
    DESCRIBE_NODE_TEMPLATE = {'method': 'DOM.describeNode', 'params': {}}
    FIND_ELEMENT_TEMPLATE = {'method': 'DOM.querySelector', 'params': {}}
    FIND_ALL_ELEMENTS_TEMPLATE = {
        'method': 'DOM.querySelectorAll',
        'params': {},
    }
    BOX_MODEL_TEMPLATE = {'method': 'DOM.getBoxModel', 'params': {}}
    RESOLVE_NODE_TEMPLATE = {'method': 'DOM.resolveNode', 'params': {}}
    REQUEST_NODE_TEMPLATE = {'method': 'DOM.requestNode', 'params': {}}
    GET_OUTER_HTML = {
        'method': 'DOM.getOuterHTML',
        'params': {},
    }
    SCROLL_INTO_VIEW_IF_NEEDED = {
        'method': 'DOM.scrollIntoViewIfNeeded',
        'params': {},
    }

    @classmethod
    def scroll_into_view(cls, object_id: str) -> dict:
        """Generates the command to scroll a specific DOM node into view."""
        command = copy.deepcopy(cls.SCROLL_INTO_VIEW_IF_NEEDED)
        command['params']['objectId'] = object_id
        return command

    @classmethod
    def get_outer_html(cls, object_id: int) -> dict:
        """Generates the command to get the outer HTML"""
        command = copy.deepcopy(cls.GET_OUTER_HTML)
        command['params']['objectId'] = object_id
        return command

    @classmethod
    def dom_document(cls) -> dict:
        """
        Generates the command to get the root DOM node of the current page.
        """
        return cls.DOM_DOCUMENT

    @classmethod
    def request_node(cls, object_id: str) -> dict:
        """Generates the command to request a specific DOM node by its object
        ID."""
        command = copy.deepcopy(cls.REQUEST_NODE_TEMPLATE)
        command['params']['objectId'] = object_id
        return command

    @classmethod
    def describe_node(cls, object_id: str) -> dict:
        """Generates the command to describe a specific DOM node."""
        command = copy.deepcopy(cls.DESCRIBE_NODE_TEMPLATE)
        command['params']['objectId'] = object_id
        return command

    @classmethod
    def box_model(cls, object_id: str) -> dict:
        """
        Generates the command to get the box model of a specific DOM node.
        """
        command = copy.deepcopy(cls.BOX_MODEL_TEMPLATE)
        command['params']['objectId'] = object_id
        return command

    @classmethod
    def enable_dom_events(cls) -> dict:
        """Generates the command to enable the DOM domain."""
        return cls.ENABLE

    @classmethod
    def get_current_url(cls) -> dict:
        """Generates the command to get the current URL of the page."""
        return RuntimeCommands.evaluate_script('window.location.href')

    @classmethod
    def find_element(
        cls,
        by: SelectorType,
        value: str,
        object_id: str = '',
    ) -> dict:
        """Generates a command to find a DOM element based on the specified
        criteria."""
        escaped_value = value.replace('"', '\\"')
        match by:
            case By.CLASS_NAME:
                selector = f'.{escaped_value}'
            case By.ID:
                selector = f'#{escaped_value}'
            case _:
                selector = escaped_value
        if object_id and not by == By.XPATH:
            script = Scripts.RELATIVE_QUERY_SELECTOR.replace(
                '{selector}', selector
            )
            command = RuntimeCommands.call_function_on(
                object_id,
                script,
                return_by_value=False,
            )
        elif by == By.XPATH:
            command = cls._find_element_by_xpath(value, object_id)
        else:
            command = RuntimeCommands.evaluate_script(
                Scripts.QUERY_SELECTOR.replace('{selector}', selector)
            )
        return command

    @classmethod
    def find_elements(
        cls,
        by: SelectorType,
        value: str,
        object_id: str = '',
    ) -> dict:
        """Generates a command to find multiple DOM elements based on the
        specified criteria."""
        escaped_value = value.replace('"', '\\"')
        match by:
            case By.CLASS_NAME:
                selector = f'.{escaped_value}'
            case By.ID:
                selector = f'#{escaped_value}'
            case _:
                selector = escaped_value
        if object_id and not by == By.XPATH:
            script = Scripts.RELATIVE_QUERY_SELECTOR_ALL.replace(
                '{selector}', escaped_value
            )
            command = RuntimeCommands.call_function_on(
                object_id,
                script,
                return_by_value=False,
            )
        elif by == By.XPATH:
            command = cls._find_elements_by_xpath(value, object_id)
        else:
            command = RuntimeCommands.evaluate_script(
                Scripts.QUERY_SELECTOR_ALL.replace('{selector}', selector)
            )
        return command

    @classmethod
    def _find_element_by_xpath(cls, xpath: str, object_id: str) -> dict:
        """Creates a command to find a DOM element by XPath."""
        escaped_value = xpath.replace('"', '\\"')
        if object_id:
            escaped_value = cls._ensure_relative_xpath(escaped_value)
            script = Scripts.FIND_RELATIVE_XPATH_ELEMENT.replace(
                '{escaped_value}', escaped_value
            )
            command = RuntimeCommands.call_function_on(
                object_id,
                script,
                return_by_value=False,
            )
        else:
            script = Scripts.FIND_XPATH_ELEMENT.replace(
                '{escaped_value}', escaped_value
            )
            command = RuntimeCommands.evaluate_script(script)
        return command

    @classmethod
    def _find_elements_by_xpath(cls, xpath: str, object_id: str) -> dict:
        """Creates a command to find multiple DOM elements by XPath."""
        escaped_value = xpath.replace('"', '\\"')
        if object_id:
            escaped_value = cls._ensure_relative_xpath(escaped_value)
            script = Scripts.FIND_RELATIVE_XPATH_ELEMENTS.replace(
                '{escaped_value}', escaped_value
            )
            command = RuntimeCommands.call_function_on(
                object_id,
                script,
                return_by_value=False,
            )
        else:
            script = Scripts.FIND_XPATH_ELEMENTS.replace(
                '{escaped_value}', escaped_value
            )
            command = RuntimeCommands.evaluate_script(script)
        return command

    @staticmethod
    def _ensure_relative_xpath(xpath: str) -> str:
        """Ensures that the XPath expression is relative."""
        return f'.{xpath}' if not xpath.startswith('.') else xpath

import copy
class NetworkCommands: """ This class encapsulates the network commands of the Chrome DevTools Protocol (CDP).
CDP allows developers to interact with the Chrome browser's internal mechanisms to inspect, manipulate, and monitor network operations, which can be invaluable for debugging web applications, testing network behaviors, and optimizing performance.
The commands defined in this class provide functionality for: - Managing browser cache and cookies. - Enabling and disabling network events. - Retrieving and modifying request and response data. - Customizing HTTP headers and user agent strings. - Blocking specific URLs to prevent unwanted network traffic. """
CLEAR_BROWSER_CACHE = {'method': 'Network.clearBrowserCache'} CLEAR_BROWSER_COOKIES = {'method': 'Network.clearBrowserCookies'} DELETE_COOKIES_TEMPLATE = {'method': 'Network.deleteCookies', 'params': {}} DISABLE = {'method': 'Network.disable'} ENABLE = {'method': 'Network.enable'} GET_COOKIES_TEMPLATE = {'method': 'Network.getCookies', 'params': {}} GET_REQUEST_POST_DATA_TEMPLATE = { 'method': 'Network.getRequestPostData', 'params': {}, } GET_RESPONSE_BODY_TEMPLATE = { 'method': 'Network.getResponseBody', 'params': {}, } SET_CACHE_DISABLED_TEMPLATE = { 'method': 'Network.setCacheDisabled', 'params': {}, } SET_COOKIE_TEMPLATE = {'method': 'Network.setCookie', 'params': {}} SET_COOKIES_TEMPLATE = {'method': 'Network.setCookies', 'params': {}} SET_EXTRA_HTTP_HEADERS_TEMPLATE = { 'method': 'Network.setExtraHTTPHeaders', 'params': {}, } SET_USERAGENT_OVERRIDE_TEMPLATE = { 'method': 'Network.setUserAgentOverride', 'params': {}, } GET_ALL_COOKIES = {'method': 'Network.getAllCookies'} SEARCH_IN_RESPONSE_TEMPLATE = { 'method': 'Network.searchInResponseBody', 'params': {}, } SET_BLOCKED_URLS = {'method': 'Network.setBlockedURLs', 'params': {}}
@classmethod def clear_browser_cache(cls): """ Command to clear the browser's cache.
This is useful when you want to ensure that your application retrieves the most up-to-date resources from the server instead of loading potentially stale data from the cache. """ return cls.CLEAR_BROWSER_CACHE
@classmethod def clear_browser_cookies(cls): """ Command to clear all cookies stored in the browser.
This can be beneficial for testing scenarios where you need to simulate a fresh user session without any previously stored cookies that might affect the application's behavior. """ return cls.CLEAR_BROWSER_COOKIES
@classmethod def delete_cookies(cls, name: str, url: str = ''): """ Creates a command to delete a specific cookie by name.
Args: name (str): The name of the cookie to delete. url (str, optional): The URL associated with the cookie. If specified, only the cookie matching both the name and URL will be deleted. If omitted, all cookies with the given name will be deleted regardless of URL.
Returns: dict: A command to delete the specified cookie. """ delete_cookies_template = copy.deepcopy(cls.DELETE_COOKIES_TEMPLATE) delete_cookies_template['params']['name'] = name if url: delete_cookies_template['params']['url'] = url return delete_cookies_template
@classmethod def disable_network_events(cls): """ Command to disable network event notifications.
Use this command when you want to temporarily suspend the emission of network events, which can be useful during specific operations where you don't want to be notified about every network request and response. """ return cls.DISABLE
@classmethod def enable_network_events(cls): """ Command to enable network event notifications.
This allows you to start receiving network-related events again after they have been disabled. It's essential to call this before you expect to receive network events. """ return cls.ENABLE
@classmethod def get_cookies(cls, urls: list[str] = []): """ Creates a command to retrieve cookies from specified URLs.
Args: urls (list[str], optional): A list of URLs for which to retrieve cookies. If not provided, cookies from all URLs will be fetched.
Returns: dict: A command to get cookies associated with the specified URLs. """ get_cookies_template = copy.deepcopy(cls.GET_COOKIES_TEMPLATE) if urls: get_cookies_template['params']['urls'] = urls return get_cookies_template
@classmethod def get_request_post_data(cls, request_id: str): """ Creates a command to retrieve POST data associated with a specific request.
Args: request_id (str): The unique identifier of the network request whose POST data is to be retrieved.
Returns: dict: A command to get the POST data for the specified request. """ get_request_post_data_template = copy.deepcopy( cls.GET_REQUEST_POST_DATA_TEMPLATE ) get_request_post_data_template['params']['requestId'] = request_id return get_request_post_data_template
@classmethod def get_response_body(cls, request_id: str): """ Creates a command to retrieve the body of a response for a specific request.
Args: request_id (str): The unique identifier of the request for which the response body is to be fetched.
Returns: dict: A command to get the response body associated with the specified request. """ get_response_body_template = copy.deepcopy( cls.GET_RESPONSE_BODY_TEMPLATE ) get_response_body_template['params']['requestId'] = request_id return get_response_body_template
@classmethod def set_cache_disabled(cls, cache_disabled: bool): """ Creates a command to enable or disable the browser cache.
Args: cache_disabled (bool): Set to True to disable caching, or False to enable it.
Returns: dict: A command to set the cache state in the browser. """ set_cache_disabled_template = copy.deepcopy( cls.SET_CACHE_DISABLED_TEMPLATE ) set_cache_disabled_template['params']['cacheDisabled'] = cache_disabled return set_cache_disabled_template
@classmethod def set_cookie(cls, name: str, value: str, url: str = ''): """ Creates a command to set a specific cookie.
Args: name (str): The name of the cookie. value (str): The value of the cookie. url (str, optional): The URL associated with the cookie. If provided, the cookie will be valid for this URL only.
Returns: dict: A command to set the specified cookie in the browser. """ set_cookie_template = copy.deepcopy(cls.SET_COOKIE_TEMPLATE) set_cookie_template['params']['name'] = name set_cookie_template['params']['value'] = value if url: set_cookie_template['params']['url'] = url return set_cookie_template
@classmethod def set_cookies(cls, cookies: list[dict]): """ Creates a command to set multiple cookies at once.
Args: cookies (list[dict]): A list of dictionaries, each representing a cookie with its properties (name, value, url, etc.).
Returns: dict: A command to set the specified cookies in the browser. """ set_cookies_template = copy.deepcopy(cls.SET_COOKIES_TEMPLATE) set_cookies_template['params']['cookies'] = cookies return set_cookies_template
@classmethod def set_extra_http_headers(cls, headers: dict): """ Creates a command to set additional HTTP headers for subsequent network requests.
Args: headers (dict): A dictionary of headers to include in all future requests.
Returns: dict: A command to set extra HTTP headers for the browser's network requests. """ set_extra_http_headers_template = copy.deepcopy( cls.SET_EXTRA_HTTP_HEADERS_TEMPLATE ) set_extra_http_headers_template['params']['headers'] = headers return set_extra_http_headers_template
@classmethod def set_useragent_override(cls, user_agent: str): """ Creates a command to override the user agent string used in network requests.
Args: user_agent (str): The user agent string to set for future network requests.
Returns: dict: A command to override the browser's user agent for network requests. """ set_useragent_override_template = copy.deepcopy( cls.SET_USERAGENT_OVERRIDE_TEMPLATE ) set_useragent_override_template['params']['userAgent'] = user_agent return set_useragent_override_template
@classmethod def get_all_cookies(cls): """ Command to retrieve all cookies stored in the browser.
This can be useful for diagnostics, testing, or ensuring that your application behaves as expected when accessing cookies. """ return cls.GET_ALL_COOKIES
@classmethod def search_in_response( cls, request_id: str, query: str, case_sensitive: bool = False, is_regex: bool = False, ): """ Creates a command to search for a specific query in the response body of a network request.
Args: request_id (str): The unique identifier of the request to search within. query (str): The string to search for within the response body. case_sensitive (bool, optional): Whether the search should be case sensitive. Defaults to False. is_regex (bool, optional): Whether the query should be treated as a regular expression. Defaults to False.
Returns: dict: A command to search the specified query within the response body of the given request. """ search_in_response_template = copy.deepcopy( cls.SEARCH_IN_RESPONSE_TEMPLATE ) search_in_response_template['params']['requestId'] = request_id search_in_response_template['params']['query'] = query search_in_response_template['params']['caseSensitive'] = case_sensitive search_in_response_template['params']['isRegex'] = is_regex return search_in_response_template
@classmethod def set_blocked_urls(cls, urls: list[str]): """ Creates a command to block specific URLs from being requested by the browser.
Args: urls (list[str]): A list of URL patterns to block. The browser will not make requests to any URLs matching these patterns.
Returns: dict: A command to set the specified URLs as blocked. """ set_blocked_urls_template = copy.deepcopy(cls.SET_BLOCKED_URLS) set_blocked_urls_template['params']['urls'] = urls return set_blocked_urls_template

class PageCommands: """ PageCommands class provides a set of commands to interact with the Page domain of the Chrome DevTools Protocol (CDP). These commands enable users to perform operations related to web pages, such as capturing screenshots, navigating to URLs, refreshing pages, printing to PDF, and enabling the Page domain.
The following operations can be performed: - Capture a screenshot of the current page. - Navigate to a specified URL. - Refresh the current page, with an option to ignore the cache. - Print the current page to a PDF document. - Enable the Page domain for further interactions.
Each method generates a command that can be sent to the browser as part of the DevTools Protocol communication. """
SCREENSHOT_TEMPLATE = { 'method': 'Page.captureScreenshot', 'params': {}, } GO_TO_TEMPLATE = {'method': 'Page.navigate', 'params': {}} REFRESH_TEMPLATE = {'method': 'Page.reload', 'params': {}} PRINT_TO_PDF_TEMPLATE = {'method': 'Page.printToPDF', 'params': {}} ENABLE_PAGE = {'method': 'Page.enable'} DISABLE_PAGE = {'method': 'Page.disable'} SET_DOWNLOAD_BEHAVIOR = { 'method': 'Page.setDownloadBehavior', 'params': {}, } HANDLE_DIALOG = {'method': 'Page.handleJavaScriptDialog', 'params': {}} CLOSE = {'method': 'Page.close'}
@classmethod def handle_dialog(cls, accept: bool = True) -> dict: """ Generates the command to handle a JavaScript dialog.
Args: accept (bool): Whether to accept the dialog. If True, the dialog will be accepted. If False, the dialog will be dismissed.
Returns: dict: The command to be sent to the browser, containing the method and parameters for handling the dialog. """ command = cls.HANDLE_DIALOG.copy() command['params']['accept'] = accept return command
@classmethod def set_download_path(cls, path: str) -> dict: """ Generates the command to set the download path for the browser.
Args: path (str): The path where the downloaded files should be saved.
Returns: dict: The command to be sent to the browser, containing the method and parameters for setting the download path. """ command = cls.SET_DOWNLOAD_BEHAVIOR.copy() command['params']['behavior'] = 'allow' command['params']['downloadPath'] = path return command
@classmethod def screenshot( cls, fmt: str = 'jpeg', quality: int = 100, clip: dict = None ) -> dict: """ Generates the command to capture a screenshot of the current page.
Args: fmt (str): The format of the image to be captured. Can be 'png' or 'jpeg'. quality (int): The quality of the image to be captured, applicable only if the format is 'jpeg'. Value should be between 0 (lowest quality) and 100 (highest quality).
Returns: dict: The command to be sent to the browser, containing the method and parameters for the screenshot. """ command = cls.SCREENSHOT_TEMPLATE.copy() command['params']['format'] = fmt command['params']['quality'] = quality if clip: command['params']['clip'] = clip return command
@classmethod def go_to(cls, url: str) -> dict: """ Generates the command to navigate to a specific URL.
Args: url (str): The URL to navigate to. It should be a valid URL format.
Returns: dict: The command to be sent to the browser, containing the method and parameters for navigation. """ command = cls.GO_TO_TEMPLATE.copy() command['params']['url'] = url return command
@classmethod def refresh(cls, ignore_cache: bool = False) -> dict: """ Generates the command to refresh the current page.
Args: ignore_cache (bool): Whether to ignore the cache when refreshing. If True, the cached resources will not be used.
Returns: dict: The command to be sent to the browser, containing the method and parameters for page refresh. """ command = cls.REFRESH_TEMPLATE.copy() command['params']['ignoreCache'] = ignore_cache return command
@classmethod def print_to_pdf( cls, scale: int = 1, paper_width: float = 8.5, paper_height: float = 11 ) -> dict: """ Generates the command to print the current page to a PDF.
Args: scale (int): The scale of the page to print. Default is 1 (100%). paper_width (float): The width of the paper to print on, in inches. Default is 8.5 inches. paper_height (float): The height of the paper to print on, in inches. Default is 11 inches.
Returns: dict: The command to be sent to the browser, containing the method and parameters for printing to PDF. """ command = cls.PRINT_TO_PDF_TEMPLATE.copy() command['params']['scale'] = scale command['params']['paperWidth'] = paper_width command['params']['paperHeight'] = paper_height return command
